    /**
     * @todo Implement testExec().
     */
    public function testExecMaskCpf()
    {
        $cpf = $this->object->exec('36328611889', '000.000.000-00');
        var_dump($cpf);
        $this->assertEquals('363.286.118-89', $cpf);
    }

    public function testExecMaskCnpj()
    {
        $cnpj = $this->object->exec('48464245000104', '###.###.###/####-##');
        var_dump($cnpj);
        $this->assertEquals('48.464.245/0001-04', $cnpj);
    }

    public function testDecimal()
    {
        $decimal = $this->object->decimal(123123123.20, '0.000,0');
        var_dump($decimal);
        $this->assertEquals('123.123.123,2', $decimal);
    }

