<?php

namespace Sped\Validation;

/**
 * Test class for Cpf.
 * Generated by PHPUnit on 2012-07-27 at 09:08:15.
 */
class CpfTest extends \PHPUnit_Framework_TestCase
{

    /**
     * @var Cpf
     */
    protected $object;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp()
    {
        $this->object = new Cpf;
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown()
    {
        
    }

    public function testValidate()
    {
        $this->assertTrue($this->object->validate('25143956153'), 'O CPF não é válido');
    }

    public function testValidateFormatted()
    {
        $this->assertTrue($this->object->validate('251.439.561-53'), 'O CPF não é válido');
    }

}

