<?php

namespace Sped;

/**
 * Test class for Validation.
 * Generated by PHPUnit on 2012-07-27 at 09:10:54.
 */
class ValidationTest extends \PHPUnit_Framework_TestCase
{

    /**
     * @var Validation
     */
    protected $object;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp()
    {
        $this->object = new Validation;
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown()
    {
        
    }

    public function testValidationCean()
    {
        $this->assertTrue($this->object->cean()->validate('7590002037010'));
    }

    public function testValidationCnpj()
    {
        $this->assertTrue($this->object->cnpj()->validate('48464245000104'));
    }

    public function testValidationCnpjFormatted()
    {
        $this->assertTrue($this->object->cnpj()->validate('48.464.245/0001-04'));
    }

    public function testValidationCpf()
    {
        $this->assertTrue($this->object->cpf()->validate('25143956153'));
    }

    public function testValidationCpfFormatted()
    {
        $this->assertTrue($this->object->cpf()->validate('251.439.561-53'));
    }

}

